import React, { Component } from "react";
import CardDisplay from "../components/CardDisplay";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = { starWarsList: "" };
  }
  componentDidMount() {
    fetch("https://swapi.py4e.com/api/people/")
      .then((resp) => resp.json())
      .then((result) => {
        console.log(result);
        return this.setState({ starWarsList: result });
      })
      .catch((err) => console.log("ooops", err));
  }

  render() {
    return (
      <div className="App">
        {/* <CardDisplay starWarsList={this.state.starWarsList} /> */}
      </div>
    );
  }
}

export default App;
