import { hover } from "@testing-library/user-event/dist/hover";
import React, { Component } from "react";
import "./Card.css";
const Card = ({ name, height, mass, gender }) => {
  var personalHeight;
  if (height <= 150) {
    personalHeight = "30%";
  } else if (151 <= height <= 200) {
    personalHeight = "60%";
  } else {
    personalHeight = "80%";
  }
  var personalMass;
  if (mass <= 50) {
    personalMass = "40px";
  } else if (mass <= 100) {
    personalMass = "80px";
  } else if (mass >= 100) {
    personalMass = "120px";
  }
  if (name === "Luke Skywalker") {
  }
  return (
    <div className="Card">
      <div className="imageCover"></div>
      <h2>{`Name : ${name}`}</h2>
      <h3>{`Height : ${height}`}</h3>
      <div className="heightBar" style={{ width: personalHeight }}></div>
      <div className="massCtn">
        <p>{`weight : ${mass}`}</p>
        <div
          className="massBar"
          style={{ width: personalMass, height: personalMass }}
        ></div>
      </div>
      <p>{`gender : ${gender}`}</p>
    </div>
  );
};

export default Card;
