import React, { Component } from "react";
import CardDisplay from "../components/CardDisplay";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = { starWarsList: "" };
  }

  async componentDidMount() {
    const goFetch = await fetch("https://swapi.py4e.com/api/people");
    const goJson = goFetch.json();
    // fetch("https://swapi.py4e.com/api/people")
    //   .then((resp) => resp.json())
    //   .then((data) => this.setState({ starWarsList: data }));
  }

  render() {
    console.log(this.state.starWarsList);
    return (
      <div className="App">
        {this.state.starWarsList !== "" ? (
          <div>
            <CardDisplay starWarsList={this.state.starWarsList} />
          </div>
        ) : undefined}
      </div>
    );
  }
}

export default App;
