import React, { Component } from "react";
import CardDisplay from "../components/CardDisplay";
import SearchBar from "../components/SearchBar";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = { starWarsList: "", search: "" };
  }

  async componentDidMount() {
    const goFetch = await fetch("https://swapi.py4e.com/api/people");
    const goJson = await goFetch.json();
    this.setState({ starWarsList: goJson });
  }
  inputGotChanged = (e) => {
    console.log(e.target.value);
    this.setState({ search: e.target.value });
  };

  render() {
    console.log(this.state);
    if (this.state.starWarsList !== "") {
      var filteredName = this.state.starWarsList.results.filter((item) => {
        return item.name.includes(this.state.search.toLocaleLowerCase());
      });
      console.log(filteredName);
    }

    return (
      <div className="App">
        <h1 className="title">Star Wars API</h1>
        <SearchBar inputGotChanged={this.inputGotChanged} />
        {this.state.starWarsList !== "" ? (
          <div>
            <CardDisplay starWarsList={this.state.starWarsList} />
          </div>
        ) : undefined}
      </div>
    );
  }
}

export default App;
