import React, { Component } from "react";
import CardDisplay from "../components/CardDisplay";
import SearchBar from "../components/SearchBar";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = { starWarsList: "" };
  }

  async componentDidMount() {
    const goFetch = await fetch("https://swapi.py4e.com/api/people");
    const goJson = await goFetch.json();
    this.setState({ starWarsList: goJson });
  }

  render() {
    console.log(this.state.starWarsList);
    return (
      <div className="App">
        <h1 className="title">Star Wars API</h1>
        <SearchBar />
        {this.state.starWarsList !== "" ? (
          <div>
            <CardDisplay starWarsList={this.state.starWarsList} />
          </div>
        ) : undefined}
      </div>
    );
  }
}

export default App;
