import React, { Component } from "react";
import "./CardDisplay.css";
import Card2 from "./Card2";
const CardDisplay = ({ starWarsList, search, select, cardFlash }) => {
  const SWLCPY = JSON.parse(JSON.stringify(starWarsList.results));
  console.log(SWLCPY);
  SWLCPY[0].rank = 0;
  SWLCPY[1].rank = 1;
  SWLCPY[2].rank = 2;
  SWLCPY[3].rank = 3;
  SWLCPY[9].rank = 4;
  SWLCPY[4].rank = 5;
  SWLCPY[5].rank = 6;
  SWLCPY[6].rank = 7;
  SWLCPY[7].rank = 8;
  SWLCPY[8].rank = 9;

  console.log("this is SWLCPy");
  let starWarsListCPY = JSON.parse(JSON.stringify(starWarsList.results));
  console.log(starWarsListCPY);
  function SortArrayByAlpha(x, y) {
    if (x.name < y.name) {
      return -1;
    }
    if (x.name > y.name) {
      return 1;
    }
    return 0;
  }
  var sortedList;
  switch (select) {
    case "popularity":
      sortedList = SWLCPY.sort(function (a, b) {
        return parseFloat(a.height) - parseFloat(b.height);
      });
      break;
    case "alphabetical":
      sortedList = starWarsListCPY.sort(SortArrayByAlpha);
      break;
    case "height":
      sortedList = starWarsListCPY.sort(function (a, b) {
        return parseFloat(a.height) - parseFloat(b.height);
      });
      break;
    case "mass":
      sortedList = starWarsListCPY.sort(function (a, b) {
        return parseFloat(a.mass) - parseFloat(b.mass);
      });
      break;
  }

  if (starWarsList !== "") {
    var filteredList = sortedList.filter((item) => {
      return item.name.toLowerCase().includes(search.toLowerCase());
    });
  } else {
    filteredList = sortedList;
  }

  const heightArray = starWarsList.results.map((item) => {
    return item.height;
  });
  const massArray = starWarsList.results.map((item) => {
    return item.mass;
  });

  let tallest = Math.max(...heightArray);
  let heaviest = Math.max(...massArray);
  let todivedBy = tallest / 100;
  let todivedByforMass = heaviest / 100;

  return (
    <div className="CardDisplay">
      {filteredList.map((item, index) => {
        return (
          <Card2
            key={index}
            name={item.name}
            mass={item.mass}
            height={item.height}
            gender={item.gender}
            todivedBy={todivedBy}
            todivedByforMass={todivedByforMass}
            cardFlash={cardFlash}
          />
        );
      })}
    </div>
  );
};

export default CardDisplay;
