import React, { Component } from "react";
import "./Card.css";
const Card = ({ name, height, mass, gender }) => {
  const personalHeight = "medium";
  if (height <= 150) {
    personalHeight = "small";
  }

  return (
    <div className="Card">
      <h2>{name}</h2>
      <h3>{height}</h3>

      <div className="heightBar" style={{}}></div>
      <p>{mass}</p>
      <p>{gender}</p>
    </div>
  );
};

export default Card;
