import React, { Component } from "react";
import "./Card2.css";
const Card2 = ({ cardFlash, name, height, mass }) => {
  console.log("card render");
  return (
    <div className="Card2">
      <div
        className="ctn1"
        style={{
          backgroundImage:
            "url(https://images.bfmtv.com/RxO3TGwT51kwE4S_0aFVsNVoKbk=/4x3:996x561/640x0/images/-76356.jpg)",
          backgroundPosition: "center",
        }}
      >
        1
      </div>
      <div className="ctn2">
        <h2>Luke Skywalker</h2>
      </div>
      <div className="ctn3">
        <p>height</p>
        <div className="hBar">
          <div
            className="hBarProgress"
            style={cardFlash ? { width: "40%" } : undefined}
          ></div>
        </div>
        <p>weight</p>
        <div className="hBar">
          <div
            className="hBarProgress"
            style={cardFlash ? { width: "80%" } : undefined}
          ></div>
        </div>
        <p>popularity</p>
        <div className="hBar">
          <div
            className={cardFlash ? "hBarProgress cardFlash" : "hBarProgress"}
          ></div>
        </div>
      </div>
    </div>
  );
};

export default Card2;
