import React, { Component } from "react";
import "./CardDisplay.css";
import Card from "./Card";
const CardDisplay = ({ starWarsList }) => {
  if (this.state.starWarsList !== "") {
    var filteredName = this.state.starWarsList.results.filter((item) => {
      return item.name
        .toLowerCase()
        .includes(this.state.search.toLocaleLowerCase());
    });
    console.log(filteredName);
  } else {
    // filteredName = this.state.starWarsList;
  }

  return (
    <div className="CardDisplay">
      {starWarsList.results.map((item, index) => {
        return (
          <Card
            key={index}
            name={item.name}
            mass={item.mass}
            height={item.height}
            gender={item.gender}
          />
        );
      })}
    </div>
  );
};

export default CardDisplay;
