import React, { Component } from "react";
import CardDisplay from "../components/CardDisplay";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = { starWarsList: "" };
  }
  componentDidMount() {
    // fetch("https://swapi.py4e.com/api/people")
    //   .then((resp) => resp.json())
    //   .then((data) => this.setState({ starWarsList: data }));

    const fetchData = async function (url) {
      const goFetch = await fetch(url);
      const goJson = await goFetch.json();
      await this.setState({ starWarsList: goJson });
    };
  }

  render() {
    console.log(this.state.starWarsList);
    return (
      <div className="App">
        {this.state.starWarsList !== "" ? (
          <div>
            <CardDisplay starWarsList={this.state.starWarsList} />
          </div>
        ) : undefined}
      </div>
    );
  }
}

export default App;
