import React, { Component } from "react";
import "./Card.css";
const Card = ({ name, height, mass, gender }) => {
  var personalHeight;
  if (height <= 150) {
    personalHeight = "30%";
  } else if (151 <= height <= 200) {
    personalHeight = "60%";
  } else {
    personalHeight = "80%";
  }
  var personalMass;
  if (mass <= 50) {
    personalMass = "30px";
  } else if (60 <= mass <= 100) {
    personalMass = "60px";
  } else {
    personalMass = "100px";
  }

  return (
    <div className="Card">
      <h2>{name}</h2>
      <h3>{height}</h3>
      <div className="heightBar" style={{ width: personalHeight }}></div>
      <p>{mass}</p>
      <div
        className="massBar"
        style={{ width: personalMass, height: personalMass }}
      ></div>

      <p>{gender}</p>
    </div>
  );
};

export default Card;
