import React, { Component } from "react";
import "./CardDisplay.css";
import Card from "./Card";
const CardDisplay = ({ starWarsList, search, select }) => {
  console.log(select);
  var sortedList;
  function SortArrayByAlpha(x, y) {
    if (x.name < y.name) {
      return -1;
    }
    if (x.name > y.name) {
      return 1;
    }
    return 0;
  }
  function SortArrayByHeight(x, y) {
    sortedList = starWarsList.results.sort(function (a, b) {
      return parseFloat(a.height) - parseFloat(b.height);
    });
  }

  switch (select) {
    case "popularity":
      sortedList = starWarsList.results;
      break;
    case "alphabetical":
      sortedList = starWarsList.results.sort(SortArrayByAlpha);
      console.log(sortedList);
      break;
    case "height":
      sortedList = starWarsList.results.sort(function (a, b) {
        return parseFloat(a.height) - parseFloat(b.height);
      });
      console.log(sortedList);
      break;
  }

  if (starWarsList !== "") {
    var filteredList = sortedList.filter((item) => {
      return item.name.toLowerCase().includes(search.toLowerCase());
    });
    console.log(filteredList);
  } else {
    filteredList = starWarsList;
  }

  const heightArray = starWarsList.results.map((item) => {
    return item.height;
  });

  let tallest = Math.max(...heightArray);
  console.log(tallest);
  let todivedBy = tallest / 100;
  console.log(todivedBy);

  return (
    <div className="CardDisplay">
      {filteredList.map((item, index) => {
        return (
          <Card
            key={index}
            name={item.name}
            mass={item.mass}
            height={item.height}
            gender={item.gender}
            todivedBy={todivedBy}
          />
        );
      })}
    </div>
  );
};

export default CardDisplay;
