import React, { Component } from "react";
import "./CardDisplay.css";
import Card from "./Card";
const CardDisplay = ({ starWarsList, search, select }) => {
  if (select === "popularity") {
    var sortedList = starWarsList;
  }
  if (sortedList !== "") {
    var filteredList = sortedList.results.filter((item) => {
      return item.name.toLowerCase().includes(search.toLowerCase());
    });
    console.log(filteredList);
  } else {
    filteredList = sortedList;
  }

  const heightArray = starWarsList.results.map((item) => {
    return item.height;
  });

  let tallest = Math.max(...heightArray);
  console.log(tallest);
  let todivedBy = tallest / 100;
  console.log(todivedBy);

  return (
    <div className="CardDisplay">
      {sortedList.map((item, index) => {
        return (
          <Card
            key={index}
            name={item.name}
            mass={item.mass}
            height={item.height}
            gender={item.gender}
            todivedBy={todivedBy}
          />
        );
      })}
    </div>
  );
};

export default CardDisplay;
