import React, { Component } from "react";
import "./Card.css";
const Card = ({ name, height, mass, gender }) => {
  var personalHeight;
  if (height <= 150) {
    personalHeight = "30%";
  } else if (151 <= height <= 200) {
    personalHeight = "60%";
  } else {
    personalHeight = "80%";
  }
  var personalMass;
  if (mass <= 50) {
    personalMass = "40px";
  } else if (mass <= 100) {
    personalMass = "80px";
  } else if (mass >= 100) {
    personalMass = "120px";
  }

  return (
    <div className="Card">
      <h2>{`Name : ${name}`}</h2>
      <h3>{`Height : ${height}`}</h3>
      <div className="heightBar" style={{ width: personalHeight }}></div>
      <div className="massCtn">
        <p>{`weight : ${mass}`}</p>
        <div
          className="massBar"
          style={{ width: personalMass, height: personalMass }}
        ></div>
      </div>
      <p>{`gender : ${gender}`}</p>
      <svg viewBox="0 0 200 200" xmlns="http://www.w3.org/2000/svg">
        <path
          fill="#FF0066"
          d="M59.2,-45.6C71,-32.5,70.8,-8.2,65.9,15.8C61.1,39.9,51.6,63.6,33.5,74C15.4,84.4,-11.2,81.4,-34.4,70.6C-57.7,59.7,-77.5,41,-81.4,19.8C-85.2,-1.3,-73,-24.9,-56.8,-39C-40.5,-53.2,-20.3,-57.9,1.7,-59.3C23.7,-60.6,47.4,-58.7,59.2,-45.6Z"
          transform="translate(100 100)"
        />
      </svg>
    </div>
  );
};

export default Card;
